function applyTheme(mode){
  const tok=THEMES[mode]||THEMES.dark;
  const r=document.documentElement;
  Object.entries(tok).forEach(([k,v])=>r.style.setProperty(k,v));
  r.setAttribute("data-theme",mode);
}

