/* ============================================================
   IMAGE OPTIMISATION
   ============================================================ */
const MAX_W=1280, QUALITY=0.70;
async function optimizeImage(file){
  try{
    const bm=await createImageBitmap(file);
    let ow=bm.width,oh=bm.height;
    if(ow>MAX_W){oh=Math.round(oh*MAX_W/ow);ow=MAX_W;}
    let cv,cx;
    if(typeof OffscreenCanvas!=="undefined"){
      cv=new OffscreenCanvas(ow,oh);cx=cv.getContext("2d");
    } else {
      cv=document.createElement("canvas");cv.width=ow;cv.height=oh;cx=cv.getContext("2d");
    }
    cx.drawImage(bm,0,0,ow,oh);bm.close();
    let bl;
    if(cv instanceof OffscreenCanvas){
      bl=await cv.convertToBlob({type:"image/webp",quality:QUALITY});
      if(!bl||!bl.size) bl=await cv.convertToBlob({type:"image/jpeg",quality:QUALITY});
    } else {
      bl=await new Promise(r=>cv.toBlob(r,"image/webp",QUALITY));
      if(!bl||!bl.size) bl=await new Promise(r=>cv.toBlob(r,"image/jpeg",QUALITY));
    }
    return(!bl||bl.size>=file.size)?file:bl;
  } catch { return file; }
}
function fmtKB(sz){ return Math.round(sz/1024)+" KB"; }

